    def explain_concept(self, field, concept):
        if field in self.scientific_concepts and concept in [c["concept"] for c in self.scientific_concepts[field]]:
            # retrieve the description of the concept
            descriptions = [c["description"] for c in self.scientific_concepts[field] if c["concept"] == concept]
            return f"**{concept}**: {descriptions[0]}"
        else:
            return "Concept not found."

    def add_concept(self, field, concept, description):
        # add a new scientific concept
        self.scientific_concepts[field].append({"concept": concept, "description": description})

